// import statements
import React, { useState } from 'react';
import {Modal, Button, TextInput, Dropdown, DateTimePicker, TextField} from '@hospitalrun/components'

const EliminationForm = () => {
    const [show, setShow] = useState(false)
    const [startDate, setStartDate] = useState(new Date())
    return (
        /* Start of form */
      <div style={{ textAlign: 'center' }}>
        <Button color="info" class="btn btn-warning" onClick={() => setShow(!show)} style={{float: 'right', marginTop: '50px', marginRight: '32px', background: '#F5DE36', color: 'black'}}>
          Add Entry
        </Button>
        <Modal
          show={show}
          toggle={() => setShow(!show)}
          title="Elimination Form"
          body={
            <div>
              <h7>Date</h7>
              <TextInput
                id="text-input"
                name="text-input"
                type="date"
                placeholder="Date"
              />
              <h7>Time</h7>
              <TextInput
                id="text-input"
                name="text-input"
                type="time"
                placeholder="Time"
              />

              <h7>Entered By</h7>
              <TextInput
                id="text-input"
                name="text-input"
                type="text"
                placeholder="name"
              />

              <h7>Ability</h7>
              <Dropdown
                text="Ability"
                size= "sm"
                items={[
                    {text: 'Independent with toileting', onClick: () => {} },
                    {text: 'Needs assistance with toileting', onClick: () => {} },
                    {text: 'Total assistance with toileting', onClick: () => {} },
                    {text: 'Incontinent of bladder', onClick: () => {} },
                    {text: 'Incontinent of bowel', onClick: () => {} },
                ]}
              />

              <h7>Devices</h7>
              <Dropdown
                text="Devices"
                size= "sm"
                items={[
                    {text: 'Bedside commode', onClick: () => {} },
                    {text: 'Urinal', onClick: () => {} },
                    {text: 'Bedpan', onClick: () => {} },
                    {text: 'Briefs/bedpans', onClick: () => {} },
                    {text: 'External catheter', onClick: () => {} },
                    {text: 'Indwelling catheter', onClick: () => {} },
                    {text: 'Suprapubic catheter', onClick: () => {} },
                ]}
              />

              <h7>Nursing Notes</h7>
              <TextField
                name="test_field" defaultValue="enter notes" rows={5}
              />
            </div>
          }
          /* Buttons */
          closeButton={{
            children: 'Submit',
            color: 'success',
            iconLocation: 'left',
            onClick: () => {
              console.log('clicked submit')
              setShow(!show)
            },
          }}
          middleButton={{
            children: 'Skip',
            color: 'warning',
            iconLocation: 'left',
            onClick: () => {
              console.log('clicked skip')
              setShow(!show)
            },
          }}
          successButton={{
            children: 'Cancel',
            color: 'danger',
            iconLocation: 'left',
            onClick: () => {
              console.log('clicked cancel')
              setShow(!show)
            },
          }}
        />
      </div>
    )
  }

  export default EliminationForm;
